INSERT INTO `products` VALUES (17,'#Intern ARISE','1791515523noun_Riseup_1789119_73942f.svg',1000.00,'<ul>\r\n<li>Debrief sessions to help you <span> analyze and learn from your internship experiences</span></li>\r\n<li>Build the right resume / CV to <span> showcase your internship, projects achievements</span> for specific job profiles</li>\r\n<li>Understand how to <span> structure your thoughts and present it well </span> in an interview</li>\r\n</ul>','Mapping your internship story with the final placements',1000.00,1,0,1,'1131716026noun_Riseup_1789119_229897.svg',4,NULL,NULL,NULL,1,2),(18,'Industry Analysis','385864858industry-blue.png',1500.00,'<ul>\r\n<li>Have <span> talking points about any industry</span> in your interview for up to 3 industries of your choice</li>\r\n<li>Ability to <span> deliver trends and insights</span> in an industry as a part of a conversation</li>\r\n<li>Deliver an <span> analysis of</span> current industry health<span> and key issues / trends</span> affecting its future growth and potential</li>\r\n<li>Ability to conduct a <span> deep dive on the industry value chain</span> and combine that with <span> demand and supply-side trends</span></li>\r\n<li>This process takes <span> 10 working days</span>, in case of <span> immediate help</span> for an application or interview, we can expedite the process to<span> 5 working days</span></li>\r\n</ul>','Learn to build your own handy high quality industry analysis',1500.00,1,0,2,'809066081385864858industry-blue.png',2,NULL,NULL,NULL,1,4),(19,'#Intern GO','1584547512noun_manager_1078230_73942f.svg',7000.00,'<ul>\r\n<li>Help you <span> choose and apply for the right internship</span> based on your skills and career goals</li>\r\n<li><span> Understand your internship project better</span> and know the skills required to excel in your internship</li>\r\n<li>Know how to convert an internship into a <span> Pre-Placement Offer / Interview</span></li>\r\n<li>Get your <span> project report and presentation reviewed </span></li>\r\n<li>The program runs all<span> throughout the application stage to even</span> after your internship completion 6 months</li>\r\n</ul>','Roadmap from internship applications to post internship',4900.00,1,0,1,'1009903553InternGO-Blue.svg',NULL,NULL,NULL,NULL,1,3),(21,'Company Analysis ','1891224196CompanyAnalysis-Blue.svg',2000.00,'<ul>\r\n<li>Have <span> talking points about any company</span> in your interview for up to <span> 5 companies</span> of your choice</li>\r\n<li>Ability to <span> deliver trends and insights</span> about a company as a part of an interview conversation</li>\r\n<li>Understand the <span> company\'s value proposition and key issues / trends</span> affecting its future growth and potential</li>\r\n<li>This process takes <span> 10 working days</span>, in case of <span> immediate help</span> for an application or interview, we can expedite the process to<span> 5 working days</span></li>\r\n</ul>','Learn to build your own handy high quality company analysis',2000.00,1,0,2,'450622384CompanyAnalysis-Blue.svg',2,NULL,NULL,NULL,1,5),(22,'Cracking the GD','665962404MockGD-Blue.svg',650.00,'<ul>\r\n<li>Learn how to <span> put your point across impactfully</span></li>\r\n<li>Learn the ebb and flow of a GD and how to <span> contribute effortlessly without upsetting the discussion</span></li>\r\n<li>Understand how to <span> open and conclude a GD</span></li>\r\n<li>Get prepared to deliver<span> different roles that you can play in a GD</span></li>\r\n<li>This process takes<span> 3 working days</span>, in case of<span> immediate help</span> for an application or GD, we can expedite the process to<span> 1 working day</span></li>\r\n</ul>','Your personalized game plan for the GDs',650.00,1,0,4,'1937730649MockGD-Blue.svg',NULL,NULL,NULL,NULL,1,7),(23,'Interview Q&A','156855932qandans-blue.png',550.00,'<ul>\r\n<li>Know the interview questions asked <span> specifically by your dream company</span></li>\r\n<li>Learn how to <span> build a compelling story</span> when answering questions in an interview</li>\r\n<li>Choose and practice <span> questions for a specific industry, company and functional profile</span> along with personality based questions</li>\r\n<li>Ability to pre-empt the series of questions that are asked in an interview to be able to <span> deliver your best</span></li>\r\n<li>This process takes <span> 3 working days </span>, incase of immediate helpfor an application or interview, we can expedite the process to<span>1 working day</span></li>\r\n</ul>','Ready answer guide for interview FAQs',550.00,1,0,4,'1147784691156855932qandans-blue.png',3,NULL,NULL,NULL,1,8),(24,'LinkedIn Diagnostic','2086850500noun_Network_206892_229897.svg',650.00,'<ul>\r\n<li>Understand how to<span> create an impactful LinkedIn profile</span> highlighting your story</li>\r\n<li>Learn how to <span>showcase your internship, projects achievements</span></li>\r\n<li>Ensure that the <span>right set of transferable and domain skills</span> are highlighted</li>\r\n<li>Get an <span>industry expert\'s view</span> of your LinkedIn profile rather than an academic review by placement committee / seniors</li>\r\n<li>This process takes <span>5 working days</span>, in case of <span>immediate help </span>;for an application or interview, we can expedite the process to <span>1 working day</span></li>\r\n</ul>','Showcase your best profile to the corporate world',650.00,1,0,3,'1345924174noun_Network_206892_229897.svg',1,NULL,NULL,NULL,1,9),(25,'Resume Diagnostic','1821577026resume-blue.png',650.00,'<ul>\r\n<li>Understand how to <span>create an impactful Resume</span> highlighting your story</li>\r\n<li>Learn how to <span>showcase your internship, projects and achievements</span></li>\r\n<li>Ensure that the <span> right set of transferable and domain skills</span> are highlighted</li>\r\n<li>Get an <span>industry expert\'s view</span> of your Resume rather than an academic review by placement committee / seniors</li>\r\n<li>This process takes <span>5 working days</span>, in case of <span>immediate help</span> for an application or interview, we can expedite the process to <span>1 working day</span></li>\r\n</ul>','Know your industry quality score and improvement areas',650.00,1,0,3,'268003464resume-blue.png',1,NULL,NULL,NULL,1,10),(27,'#Intern PRO','863209728Internpro-green.png',2000.00,'<ul>\r\n<li>Step by step guidance on how to <span> address different aspects of an internship </span> new reporting manager, new organization and hierarchy</li>\r\n<li><span> Detailed guide and templates </span> to help you through different phases of your internships</li>\r\n<li><span> Weekly interaction sessions</span> with industry experts to help you assess your progress in the internship</li>\r\n<li>The program runs all<span> throughout the 8 or 12 week internship period</span></li>\r\n</ul>','Unlock your best performance in your internship',2000.00,1,0,1,'1628920979InternPRO-Blue.svg',4,NULL,NULL,NULL,1,1),(28,'#Intern ACE','527561065internACE-Green.png',4000.00,'<ul>\r\n<li>An <span> assessment of cognitive and behavioral (EQ/EI) skills</span> to build a student-specific profile in terms of career preparedness</li>\r\n<li>Get a <span> fitment analysis of industry, company and job profile</span> you are the best fit for</li>\r\n<li><span>Build the appropriate resume / CV</span> to meet job profile and tell your story right</li>\r\n<li>Learn how to <span> exhibit best body language</span> and maintain <span>confidence in interviews and GDs</span></li>\r\n<li>Understand how to<span> structure your thoughts and present it well </span> in an interview</li>\r\n<li>The program runs all throughout the application stage and only stops when you <span> secure your internship offer 3 months</span></li>\r\n</ul>','Help you target and crack the right internship',4000.00,1,0,1,'184621254InternACE-Blue.svg',4,NULL,NULL,NULL,1,0),(30,'Mock Interview','706862055noun_interview_544347_229897.png',1000.00,'<ul>\r\n